mapper.select:
  	select
  	
    from FB_WorkItem
    
		  
			AND ObjectId = #{objectid,jdbcType=CHAR}
		  
		  
	        AND BizObjectId = #{bizobjectid,jdbcType=CHAR}
	      
	      
	        AND BizObjectSchemaCode = #{bizobjectschemacode,jdbcType=VARCHAR}
	      
	      
	        AND ActivityCode = #{activitycode,jdbcType=VARCHAR}
	      
	      
	        AND ActivityName = #{activityname,jdbcType=VARCHAR}
	      
	      
	        AND Originator = #{originator,jdbcType=CHAR}
	      
	      
	        AND Receiver = #{receiver,jdbcType=CHAR}
	      
	      
	        AND Sender = #{sender,jdbcType=CHAR}
	      
	      
	        AND Finisher = #{finisher,jdbcType=CHAR}
	      
	      
	        AND ReceiveTime = #{receivetime,jdbcType=TIMESTAMP}
	      
	      
	        AND StartTime = #{starttime,jdbcType=TIMESTAMP}
	      
	      
	        AND FinishTime = #{finishtime,jdbcType=TIMESTAMP}
	      
	      
	        AND CreateTime = #{createtime,jdbcType=TIMESTAMP}
	      
	      
	        AND UpdateTime = #{updatetime,jdbcType=TIMESTAMP}
	      
	      
	        AND State = #{state,jdbcType=INTEGER}
	      
	      
	        AND EitherOrAll = #{eitherorall,jdbcType=INTEGER}
	      
	      
	        AND InstanceId = #{instanceid,jdbcType=CHAR}
